const Callejero = require('../../lib/Callejero.js');
const callejeroCargado = require('../../src/callejero.json');
let callejero = Callejero.init(callejeroCargado).then((res) => {
    console.log('Initialized Callejero');
    callejero = res;
    console.log (verCalle(1030));
});

const verCalle = (codigo) => {
  return callejero.buscarPorCodigo(codigo)[0].nombre;
};
