/* global describe, it, before */

import chai from 'chai';
import core from '../lib/usig-core.min.js';

chai.expect();

const expect = chai.expect;

describe('En base a una instancia de la libreria Core', () => {
  it('deberia agregar \ a "\'" y "/" del texto', () => {
    expect(core.escape("'test'")).to.be.equal("\\'test\\'");
  });
  it('deberia completar el string al tamaÃ±o especificado', () => {
    expect(core.leftPad("123", 5, "-")).to.be.equal("--123");
  });
  it('deberia reemplazar todos los caracteres especificados', () => {
    expect(core.translate("string to be translated", "aeiou", "AEIOU")).to.be.equal("strIng tO bE trAnslAtEd");
  });
});
